function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),i=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),i.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((t=>{t.addEventListener("click",(function(){s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked"))}))}function reviewsSlider(){if(!document.querySelector(".reviews"))return null;new Swiper(".reviews__slider",{slidesPerView:1.1,spaceBetween:10,loop:!0,pagination:{el:".reviews__slider-pagination",type:"bullets",clickable:!0},breakpoints:{320:{slidesPerView:1.1,spaceBetween:10},767:{slidesPerView:2,spaceBetween:20}}})}burgerMenu(),window.matchMedia("(max-width: 992px)").matches&&reviewsSlider();